<script setup lang="ts">
  import {computed, defineEmits, onMounted, reactive, ref, watch} from "vue"
  import {defineProps} from "vue";
  import Icon from "~/components/kline/icon"
  import {type Period, type SymbolInfo} from "~/composables/types"
  import {useKlineLocal} from "~/stores/klineLocal";
  import {getDefaults} from "~/config";
  import {useAuthState} from "~/composables/auth";
  import {useKlineStore} from "~/stores/kline";
  import {Chart} from "klinecharts";
  
  const period_bar = ref()
  
  const props = defineProps<{
    customLoad: boolean
    hasRight: boolean
  }>()
  const emit = defineEmits<{
    loadData: []
  }>()
  const store = useKlineStore()
  const klocal = useKlineLocal()
  const {authData, authToken, authStatus} = useAuthState()
  
  function isFullscreen() {
    if(!process.client)return false;
    let doc = (document as any)
    let fullscreenElement =
        doc.fullscreenElement ||
        doc.mozFullScreenElement ||
        doc.webkitFullscreenElement ||
        doc.msFullscreenElement;
  
    return fullscreenElement != undefined;
  }
  
  const defaults = getDefaults();
  var fullScreen = ref(isFullscreen());
  const showName = ref(defaults.symbol.shortName)
  const period = ref(defaults.period)
  const showSymbolModal = ref(false)
  const showPeriodModal = ref(false)
  const showIndSearchModal = ref(false)
  const showSettingModal = ref(false)
  const showScreenShotModal = ref(false)
  const showTimezoneModal = ref(false)
  const showI18nModal = ref(false)
  const screenShotUrl = ref('')
  
  onMounted(() => {
    watch(klocal.symbol, (symbol) => {
      showName.value = symbol.shortName ?? symbol.name ?? symbol.ticker
    }, {immediate: true})
    watch(klocal.period, (p) => {
      period.value = p
    }, {immediate: true})
  })

  function clickProfile() {
    if (authStatus.value <= 0) {
      window.parent.postMessage('login')
    } else {
      window.parent.postMessage('showProfile')
    }
  }

  function toggleRight(){
    klocal.showRight = !klocal.showRight
    window.parent.postMessage('toggleSide')
  }

  function exitLogin(){
    authData.value = null
    authToken.value = null
  }
  
  function toggleTheme(){
    klocal.theme = klocal.theme === 'light' ? 'dark' : 'light';
  }
  
  
  function clickPeriod(item: Period){
    klocal.setPeriod(item)
  }
  
  
  function clickScreenShot(){
    let bgColor = klocal.theme === 'dark' ? '#151517' : '#ffffff'
    screenShotUrl.value = store.chart?.getConvertPictureUrl(true, 'jpeg', bgColor) ?? ''
    showScreenShotModal.value = true
  }
  
  function enterFullscreen() {
    if(!process.client)return;
    let elem = (period_bar.value.parentElement.parentElement as any);
    if (elem.requestFullscreen) {
      elem.requestFullscreen();
    } else if (elem.mozRequestFullScreen) { /* Firefox */
      elem.mozRequestFullScreen();
    } else if (elem.webkitRequestFullscreen) { /* Chrome, Safari and Opera */
      elem.webkitRequestFullscreen();
    } else if (elem.msRequestFullscreen) { /* IE/Edge */
      elem.msRequestFullscreen();
    }
    fullScreen.value = true;
  }
  
  function exitFullscreen() {
    if(!process.client)return;
    let elem = (document as any);
    const exitFullscreen = elem.exitFullscreen ?? elem.mozCancelFullScreen ??
        elem.webkitExitFullscreen ?? elem.msExitFullscreen;
    exitFullscreen.call(elem);
    fullScreen.value = false;
  }
  
  function toggleFullscreen() {
    if (fullScreen.value) {
      exitFullscreen()
    } else {
      enterFullscreen()
    }
  }
  </script>
  
